var mysql=require('mysql');
function connectServer(){
    var client=mysql.createConnection({
        host:'localhost',
        user:'root',
        password:'123456',
        database:'smartCampus'
    })
    return client;
}
function  loginFun(client,admin_id,callback){
    //client为一个mysql连接对象
    client.query('select admin_psd from admin where admin_id="'+admin_id+'"',function(err,results,fields){
        if(err) throw err;
        callback(results);
    });
}

exports.connect = connectServer;
exports.loginFun  = loginFun;